mapper.select:
SELECT r.rental_id rentalId, r.rental_date rentalDate, CONCAT(c.first_name, " ", c.last_name) name, r.return_Date returnDate, r.staff_id staffId FROM rental r LEFT JOIN customer c on r.customer_id=c.customer_id
r.inventory_id=#{inventoryId}
ORDER BY r.rental_date desc

SELECT COUNT(i.inventory_id) FROM inventory i LEFT join (select * from rental where (rental_date) in (select max(rental_date) from rental group by inventory_id)) r ON i.inventory_id = r.inventory_id JOIN film f ON i.film_id = f.film_id
AND i.store_id=#{storeId}
AND f.title LIKE CONCAT('%',#{searchWord},'%')
AND i.inventory_id=#{inventoryId}

SELECT i.inventory_id inventoryId, f.title title, f.film_id filmId, i.store_id storeId, f.rental_rate rentalRate, f.rating rating, if(r.rental_date IS NULL, 'T', '') rentalDate, if(r.return_date IS NULL, 'T', '') returnDate, DATEDIFF(CURRENT_DATE(), r.rental_date)-f.rental_duration overdue FROM inventory i LEFT join (select * from rental where (rental_date) in (select max(rental_date) from rental group by inventory_id)) r ON i.inventory_id = r.inventory_id JOIN film f ON i.film_id = f.film_id
AND i.store_id=#{storeId}
AND f.title LIKE CONCAT('%',#{searchWord},'%')
AND i.inventory_id=#{inventoryId}
GROUP BY i.inventory_id LIMIT #{beginRow}, #{rowPerPage}
